void define_ast_module() {
    GraceObject *ast_module = alloc_userobject(26);
    ast_module->origin = OBJECT_USER;
    ast_module->flags = FLAG_IMMORTAL;
    ast_module->members[0].name = "asString";
    ast_module->members[0].func = &graceAST_asString;
    ast_module->members[1].name = "nil";
    ast_module->members[1].func = &ASTMod_nil;
    ast_module->members[2].name = "one(1)";
    ast_module->members[2].func = &ASTMod_one;
    ast_module->members[3].name = "cons(2)";
    ast_module->members[3].func = &ASTMod_cons;
    ast_module->members[4].name = "objectConstructor(2)";
    ast_module->members[4].func = &ASTMod_objectConstructor;
    ast_module->members[5].name = "numberNode(1)";
    ast_module->members[5].func = &ASTMod_numberNode;
    ast_module->members[6].name = "stringNode(1)";
    ast_module->members[6].func = &ASTMod_stringNode;
    ast_module->members[7].name = "part(2)";
    ast_module->members[7].func = &ASTMod_part;
    ast_module->members[8].name = "lexicalRequest(1)";
    ast_module->members[8].func = &ASTMod_lexicalRequest1;
    ast_module->members[9].name = "lexicalRequest(2)";
    ast_module->members[9].func = &ASTMod_lexicalRequest2;
    ast_module->members[10].name = "explicitRequest(3)";
    ast_module->members[10].func = &ASTMod_explicitRequest;
    ast_module->members[11].name = "block(2)";
    ast_module->members[11].func = &ASTMod_block;
    ast_module->members[12].name = "methodDecl(4)";
    ast_module->members[12].func = &ASTMod_methodDecl;
    ast_module->members[13].name = "varDecl(4)";
    ast_module->members[13].func = &ASTMod_varDecl;
    ast_module->members[14].name = "defDecl(4)";
    ast_module->members[14].func = &ASTMod_defDecl;
    ast_module->members[15].name = "assign(2)";
    ast_module->members[15].func = &ASTMod_assign;
    ast_module->members[16].name = "returnStmt(1)";
    ast_module->members[16].func = &ASTMod_returnStmt;
    ast_module->members[17].name = "identifierDeclaration(2)";
    ast_module->members[17].func = &ASTMod_identifierDeclaration;
    ast_module->members[18].name = "interpString(3)";
    ast_module->members[18].func = &ASTMod_interpString;
    ast_module->members[19].name = "comment(1)";
    ast_module->members[19].func = &ASTMod_comment;
    ast_module->members[20].name = "importStmt(2)";
    ast_module->members[20].func = &ASTMod_importStmt;
    ast_module->members[21].name = "dialectStmt(1)";
    ast_module->members[21].func = &ASTMod_dialectStmt;
    ast_module->members[22].name = "typeDecl(2)";
    ast_module->members[22].func = &ASTMod_typeDecl;
    ast_module->members[23].name = "methSig(2)";
    ast_module->members[23].func = &ASTMod_methSig;
    ast_module->members[24].name = "interfaceCons(1)";
    ast_module->members[24].func = &ASTMod_interfaceCons;

    struct ImportRecord *rec = malloc(sizeof(struct ImportRecord));
    rec->path = "ast";
    rec->module = ast_module;
    rec->next = imported_modules;
    imported_modules = rec;
    ref_inc(ast_module);
}

// From a given source file, return a graceASTWrapper containing the parse tree
// of the code in that file in its data field.
//
// Evaluates the parser module, then invokes its parse method and returns the
// result directly.
ASTNode *do_parse(Context ctx, char *sourcefile) {
    GraceObject *module = evaluate(program, &ctx);
    ref_inc(module);

    FILE *f = fopen(sourcefile, "r");
    if (!f) {
        perror("Could not open source file");
        exit(1);
    }
    fseek(f, 0, SEEK_END);
    long fsize = ftell(f);
    fseek(f, 0, SEEK_SET);
    char *string = malloc(fsize + 1);
    fread(string, 1, fsize, f);
    fclose(f);
    string[fsize] = 0;

    GraceObject *parseTree = request(&ctx, module, "parse(1)", graceString(string), 0);
    // dump_object(parseTree);
    ASTNode *node = parseTree->data;
    return node;
}

